import React, { Component } from 'react'
import Board from './Board';

export default class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            xIsNext: true,
            stepNumber: 0,
            history: [
                { squares: Array(9).fill(null) }
            ],
            scores: {X:0, O:0}
        }
    }

    jumpTo(step){
        this.setState({
            stepNumber: step,
            xIsNext: (step%2)===0
        })
    }

    updateScore(winner){
         const score_history = this.state.scores

          let x_scores = score_history['X']
          let y_scores = score_history['O']

          if (winner === 'X'){
            x_scores = x_scores + 1
          } else {
            y_scores = y_scores + 1
          }

          this.setState({
                scores: {X: x_scores, O:y_scores}
            });
      }

    handleClick(i) {
        //const score_history = this.state.scores
        const history = this.state.history.slice(0, this.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        const winner = calculateWinner(squares);
        if (winner || squares[i]) {
            return;
        }

        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            history: history.concat({
                squares: squares
            }),
            xIsNext: !this.state.xIsNext,
            stepNumber: history.length
        });

    }

    render() {
        const score_history = this.state.scores
        const history = this.state.history;
        const current = history[this.state.stepNumber];
        const winner = calculateWinner(current.squares);
        const moves = history.map((step, move) => {
            const desc = move ? 'Go to #' + move : 'Start the Game';
            return (
                <li key={move}>
                    <button onClick={() => { this.jumpTo(move) }}>
                        {desc}
                    </button>
                </li>
            )
        });
        this.updateScore(winner)
        let status;
        if (winner) {
            //status = 'Winner is ' + winner + '!';
            status = "Winner: " + winner.player + " @ " + winner.line;
        } else if (!current.squares.includes(null)) {
            status = "Tie Game!";
        } else {
            status = 'Next Player is ' + (this.state.xIsNext ? 'X' : 'O');
        }

        let scores
        scores = "SCORES  - Player X: " + score_history['X'] + " wins,  " + "Player O: " + score_history['O'] + 'wins'

        return (
            <div className="game">

            <div className="game-status">
                <div>{scores}</div>
                <div>{status}</div>
            </div>

                <div className="game-board" >
                    <Board winningSquares={winner ? winner.line : []}
                      squares={current.squares}
                      onClick={i => this.handleClick(i)}
                    />
                </div>
                <div className="game-info">
                    <ul>{moves}</ul>
                </div>

            </div>
        )
    }
}

function calculateWinner(squares) {
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
    ];

    // for (let i = 0; i < lines.length; i++) {
    //     const [a, b, c] = lines[i];
    //     if (squares[a] && squares[a] === squares[b] && squares[b] === squares[c]) {
    //         return squares[a];
    //     }
    // }

    for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
          return { player: squares[a], line: [a, b, c] };
        }
      }

    return null;
}
