var sqlite3 = require('sqlite3').verbose();
var db = new sqlite3.Database(':memory:');

db.serialize(function() {
    db.run("CREATE TABLE IF NOT EXISTS score_history(player TEXT, score INT)");

    var stmt = db.prepare("INSERT INTO score_history(player, score) VALUES (?, ?)");
    stmt.run("X " , 0);
    stmt.run("O " , 0);
    stmt.finalize();

    db.each("SELECT * FROM score_history", function(err, row) {
        console.log(row.player + " " + row.score );
    });
});

db.close();
