class ScoresRepository {
  constructor(dao) {
    this.dao = dao
  }

  createTable() {
    const sql = `
      CREATE TABLE IF NOT EXISTS score_history(player TEXT, score INT)`
    return this.dao.run(sql)
  }
}

module.exports = ScoresRepository;
